import React, {useState} from 'react'

import InputField from '../UI/InputField'
import UIButton from '../UI/UIButton'
import ExternalServices from '../UI/ExternalServices'

function RegisterForm({error}) {
    const [registerDetails, setRegisterDetails] = useState({name:"", lastname:"",email:"",password:""})
    const tak = true;
    const submitHandler = e => {
        e.preventDefault();
        RegisterHandle(registerDetails);
        if(tak) {error=true}
    }

    const RegisterHandle = registerDetails => {
        if (registerDetails.name !== "" && registerDetails.name !== null &&
            registerDetails.lastname !== "" && registerDetails.lastname !== null &&
            registerDetails.email !== "" && registerDetails.email !== null &&
            registerDetails.password !== "" && registerDetails.password !== null
            ){
            console.log(registerDetails);
        }
    }

    return (
        <form className="register-form" onSubmit={submitHandler}>
            <h1>Welcome do Isardo!</h1>
            <h2>Register your Account</h2>
            <p>Name</p>
            <InputField type={"text"} placeholder={"e.g. John"} name={"name"}  value={registerDetails.name} onChange={e => setRegisterDetails({...registerDetails, name: e.target.value})} error={true}/>
            <p>Lastname</p>
            <InputField type={"text"}  placeholder={"e.g. Doe"} name={"lastname"} value={registerDetails.lastname} onChange={e => setRegisterDetails({...registerDetails, lastname: e.target.value})} />
            <p>Mail</p>
            <InputField type={"email"} placeholder={"e.g. johndoe@yahooo.com"} name={"email"} value={registerDetails.email} onChange={e => setRegisterDetails({...registerDetails, email: e.target.value})}/>
            <p>Password</p>
            <InputField type={"password"} placeholder={"Enter Your Password"} name={"password"} value={registerDetails.password} onChange={e => setRegisterDetails({...registerDetails, password: e.target.value})}/>
            <UIButton /*type={"Submit"}*/ text={"Sign Up"} width={"500px"}  margin={"40px 0 0 0"}/>
            <a className={"create-account-external"}>or create accout with</a>
            <ExternalServices/>
        </form>
    )
}

export default RegisterForm
