import React, {useState} from 'react'

const InputField = (props) => {

    const [isActive, setActive] = useState(true);
    const handleToggle = () => {
        setActive(!isActive);
    }

    return (
        <div>
        <input className={
            "input-class"+(isActive&&props.value!=="" ? " input-class--active" : "")+(" input-class--error")
            } type={props.type} placeholder={props.placeholder} value={props.value} onChange={props.onChange}></input>
        </div>
        )
}

// input-class--error

export default InputField
