import React, {useState} from 'react'
import RegisterForm from './Components/Main/RegisterForm'
import Students from './Components/Main/Students'

function App() {
  const [userLoggodenIn, setUser] = useState(true);
  const [error, setError] = useState("");

  function display() {
    if(userLoggodenIn) {
      return <Students/>
    } 
    else if(!userLoggodenIn) {
      return <RegisterForm error={error}/>;
      <div id="login-poster-left"></div>
      // class--hidden
    }
  }
  return (
    <>
    {display()}
    </>
  );
}
//login-poster-left
export default App;
