import React, {useState} from 'react'

const InputField = (props) => {

    const [isActive, setActive] = useState(true);
    const handleToggle = () => {
        setActive(!isActive);
    }

    return (
        <div>
        <input className={"input-class input-class--error"}/*{'input-class'}*/ type={props.type} placeholder={props.placeholder} value={props.value} onChange={props.onChange}></input>
        <button onClick={handleToggle}>xD</button>
        </div>
        )
}
// {"input-class"+(isActive ? "" : ", input-class--error")}
// input-class--error

export default InputField
