import React, {useState} from 'react'

const InputField = (props) => {

    const [isAtive, setActive] = useState("false");
    const handleToggle = () => {
        setActive(!isActive);
    }

    return (
        <input className={isActive ? "input-class" : "input-class--error"}/*{'input-class'}*/ type={props.type} placeholder={props.placeholder} value={props.value} onChange={props.onChange}></input>
    )
}

// input-class--error

export default InputField
