import React, {useState} from 'react'

import InputField from '../InputField'
import UIButton from '../UIButton'

export default function LoginForm() {
function RegisterForm({Login,error}) {
    const [registerDetails, setRegisterDetails] = useState({name:"", lastname:"",email:"",password:""})
    const submitHandler = e => {
        e.preventDefault();
        Login(registerDetails);
        return submitHandler;
    }
}
    
    return (
        <form className="register-form" onSubmit={submitHandler}>
            <h1>Welcome do Isardo!</h1>
            <h2>Register your Account</h2>
            <p>Name</p>
            <InputField type={"text"} placeholder={"e.g. John"} name={"name"}/>
            <p>Lastname</p>
            <InputField type={"text"} placeholder={"e.g. Doe"} name={"lastname"}/>
            <p>Mail</p>
            <InputField type={"email"} placeholder={"e.g. johndoe@yahooo.com"} name={"email"}/>
            <p>Password</p>
            <InputField type={"password"} placeholder={"Enter Your Password"} name={"password"}/>
            <UIButton /*type={"Submit"}*/ text={"Sign Up"} width={"500px"}  margin={"40px 0 0 0"}/>
            <a>or create accout with</a>
        </form>
    )
}
