import React, {useState} from 'react'

import InputField from '../InputField'
import UIButton from '../UIButton'

function RegisterForm({error}) {
    const [registerDetails, setRegisterDetails] = useState({name:"", lastname:"",email:"",password:""})
    const submitHandler = e => {
        e.preventDefault();
        Register(registerDetails);
    }

    const Register = registerDetails => {
        if (registerDetails !== null && registerDetails!=="")
            console.log(registerDetails);
    }

    return (
        <form className="register-form" onSubmit={submitHandler}>
            <h1>Welcome do Isardo!</h1>
            <h2>Register your Account</h2>
            <p>Name</p>
            <InputField type={"text"} placeholder={"e.g. John"} name={"name"}  value={registerDetails.name} onChange={e => setRegisterDetails({...registerDetails, name: e.target.value})} />
            <p>Lastname</p>
            <InputField type={"text"}  placeholder={"e.g. Doe"} name={"lastname"} value={registerDetails.lastname} onChange={e => setRegisterDetails({...registerDetails, lastname: e.target.value})}/>
            <p>Mail</p>
            <InputField type={"email"} placeholder={"e.g. johndoe@yahooo.com"} name={"email"} value={registerDetails.email} onChange={e => setRegisterDetails({...registerDetails, email: e.target.value})}/>
            <p>Password</p>
            <InputField type={"password"} placeholder={"Enter Your Password"} name={"password"} value={registerDetails.password} onChange={e => setRegisterDetails({...registerDetails, password: e.target.value})}/>
            <UIButton /*type={"Submit"}*/ text={"Sign Up"} width={"500px"}  margin={"40px 0 0 0"}/>
            <a>or create accout with</a>
        </form>
    )
}

export default RegisterForm
